import React, { useState } from 'react'
import { Typography, Avatar, Button, Paper, Grid, Container } from "@material-ui/core";
import { GoogleLogin, googleLogout } from "@react-oauth/google";
import {useHistory} from "react-router-dom";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined"
import jwt_decode from "jwt-decode"
import useStyles from "./style";
import Input from './Input';
import { useDispatch, useSelector } from 'react-redux';


const Auth = () => {
    const classes = useStyles();
    const [showPassword, setShowPassword] = useState(false);
    const [isSignUP, setIsSignup] = useState(false);
    const dispatch = useDispatch();
    const history= useHistory();

    const handleShowPassword = () => {
        setShowPassword((prevshowPassword) => {
            prevshowPassword = !prevshowPassword

        }
        )
    }
    const handleSubmit = () => {

    }
    const handleChange = () => {

    }
    const switchMode = () => {
        setIsSignup((prevIsSignup) =>


            prevIsSignup = !prevIsSignup,
            setShowPassword(false)

        )

    }


    const createOrGetUser = async (response) => {
        const userObject = jwt_decode(response.credential);

        const { name, sub, picture } = userObject;
        const user = {
            _id: sub,
            _type: 'user',
            userName: name,
            image: picture
        }
        try {
            dispatch({type:'AUTH', data: {userObject}});
            history.push('/');

        } catch (error) {
            console.log(error);

        }

    }






    return (
        <Container component="main" maxWidth="xs">
            <Paper className={classes.paper} elevation={3} >
                <Avatar className={classes.avatar}>
                    <LockOutlinedIcon />
                </Avatar>
                <Typography variant="h5">{isSignUP ? 'Sign Up' : 'Sign In'}</Typography>
                <form className={classes.form} onSubmit={handleSubmit}>
                    <Grid container spacing={2}>
                        {
                            isSignUP && (
                                <>
                                    <Input name="First Name" label="First name" handleChange={handleChange} autoFocus half />
                                    <Input name="First Name" label="First name" handleChange={handleChange} half />
                                </>
                            )
                        }
                        <Input name="email" label="Email address" handleChange={handleChange} type="email" />
                        <Input name="password" label="password" handleChange={handleChange} type={showPassword ? "text" : "password"} handleShowPassword={handleShowPassword} />
                        {isSignUP && <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange} type="password" />}
                    </Grid>
                    <GoogleLogin onSuccess={(response) => {


                        createOrGetUser(response)
                    }} onError={() => {
                        console.log("error");
                    }} />
                    <Button type="submit" className={classes.submit} fullWidth variant='contained' color="primary">
                        {isSignUP ? "SignUp" : "SignIn"}
                    </Button>

                    <Grid container justify='flex-end'>
                        <Grid item>
                            <Button onClick={switchMode}>
                                {isSignUP ? "Already have an account?Sign In" : "Don't have an account?Sign Up"}
                            </Button>


                        </Grid>

                    </Grid>


                </form>

            </Paper>

        </Container>
    )
}

export default Auth
